import React from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';

import { getCartItemsLength, getTotalCartItems } from 'redux/selectors';

const Basket = ({ totalBasketCount, totalPrice }) => {
  return (
    <div className='cart'>
      <div className='dropdown'>
        <Link
          to='/basket'
          id='dLabel'
          className='btn btn-inverse btn-block btn-lg'
        >
          <i className='fa fa-fa-shopping-cart'></i>
          <span>{totalBasketCount} item(s) - ${totalPrice}</span>
        </Link>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    totalBasketCount: getCartItemsLength(state),
    totalPrice: getTotalCartItems(state)
  };
};

export default connect(mapStateToProps)(Basket);