import {  ADD_PHONE_TO_BASKET } from 'redux/actions/actions-type';

const initialState = [];

export default (state = initialState, {type, payload}) => {
  switch(type) {
    case ADD_PHONE_TO_BASKET:
      const newState = [...state];
      newState.push(payload);
      return newState;
    default:
     return state;
  };
};